mapper.update:
update goods_sku set status = 1
where id in

    #{item}

update goods_sku set status = 0
where id in

    #{item}

mapper.select:
select if(spu.show_sales > -1 ,spu.show_sales, sum(sku.sales_volume)) totalSales from goods_sku sku
LEFT JOIN goods_spu spu on spu.id = sku.spu_id
where spu_id = #{id}
